package com.example.a51task

import android.os.Bundle
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.a51task.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding

    private val dummyNews = listOf(
        News(
            "Top News 1",
            "data:image/jpeg;base64,",
            "Minecraft description",
            related = listOf(
                News("Related 1", "https://upload.wikimedia.org/wikipedia/en/5/51/Minecraft_cover.png", "Rel 1"),
                News("Related 2", "https://upload.wikimedia.org/wikipedia/en/4/4d/FIFA_23_Cover.jpg", "Rel 2")
            )
        ),
        News(
            "Top News 2",
            "https://upload.wikimedia.org/wikipedia/en/4/4d/FIFA_23_Cover.jpg",
            "FIFA 23 description"
        )
    )

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        binding.topStoriesRecycler.layoutManager = LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
        binding.topStoriesRecycler.adapter = NewsAdapter(dummyNews) { openFragment(it) }

        binding.newsRecycler.layoutManager = LinearLayoutManager(this, LinearLayoutManager.HORIZONTAL, false)
        binding.newsRecycler.adapter = NewsAdapter(dummyNews) { openFragment(it) }
    }

    private fun openFragment(news: News) {
        val fragment = NewsDetailFragment.newInstance(news)
        supportFragmentManager.beginTransaction()
            .replace(R.id.fragmentContainer, fragment) // âœ… Target the correct container
            .addToBackStack(null)
            .commit()
    }
}
